import React from "react";
import logo from "./logo.svg";
import "./App.css";
import Lifecycles from "/lifecycles.component.js";

class App extends React.Components {
  constructor() {
    super();

    this.state = {
      showChild: true,
      text: "",
    };
  }

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <img src={logo} className='App-logo' alt='logo' />
          <button
            onClick={() =>
              this.setstate((state) => ({ showChild: !state.showChild }))
            }
          >
            {" "}
            Toggle LifeCycles
          </button>

          <button
            onClick={() =>
              this.setState((state) => ({
                text: state.text + "-hello",
              }))
            }
          >
            Update Text
          </button>
          {this.state.showChild ? <Lifecycles text={this.state.text} /> : null}
        </header>
      </div>
    );
  }
}

export default App;
