import React from "react";
import logo from "./logo.svg";
import "./App.css";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      meaningOfLife: 47,
    };
  }

  handleClick = () => {
    this.setState(
      (prevState, prevProps) => {
        return { meaningOfLife: prevState.meaningOfLife + prevProps.increment };
      },
      () => console.log(this.state.meaningOfLife)
    );
  };

  moveClick = () => {
    this.setState({ meaningOfLife: this.state.meaningOfLife + 15 });
  };

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <img src={logo} className='App-logo' alt='logo' />
          <p>{this.state.meaningOfLife}</p>
          <button className='change-age' onClick={this.handleClick}>
            Update State
          </button>

          <button className='changeColor' onClick={this.changeColor}>
            Change Color
          </button>
        </header>
      </div>
    );
  }
}

export default App;
